mapper.select:
select board_no ,board_writer ,board_type ,board_title ,board_content ,enroll_date ,count ,status ,community_good ,(select count(*) from reply where reply.board_no = board_all.board_no) replyCnt ,(select attachment.FILE_NAME from attachment
where attachment.board_no = board_all.board_no and ROWNUM <= 1
) fileName from board_all where board_type = '4' and status = 'Y' and board_title like '%'||#{search}||'%' order by board_no desc

select board_no ,board_writer ,board_type ,board_title ,board_content ,enroll_date ,count ,status ,community_good ,(select count(*) from reply where reply.board_no = board_all.board_no) replyCnt ,(select attachment.FILE_NAME from attachment
where attachment.board_no = board_all.board_no and ROWNUM <= 1
) fileName from board_all where board_type = '4' and status = 'Y' and board_title like '%'||#{boardTitle}||'%' and BOARD_WRITER = #{boardWriter} order by board_no desc

mapper.update:
update board_all set 
	board_title = #{boardTitle},
	board_content = #{boardContent}
where board_no = #{boardNo}

mapper.select:
select file_name, board_no, member_no, file_origin_name, file_path, file_size, file_upload_date
from attachment
where board_no = #{pageNo}

select file_name, board_no, member_no, file_origin_name, file_path, file_size, file_upload_date
from attachment
where board_no = #{boardNo}

		select file_name, board_no, member_no, file_origin_name, 
			file_path, file_size, file_upload_date
					from (
						 select file_name, board_no, member_no, file_origin_name, 
                   file_path, file_size, file_upload_date, ROW_NUMBER() OVER(PARTITION BY board_no order by file_name) rn
               from attachment
			)where board_no in 

       			#{no}


       and rn = 1 order by board_no desc

