mapper.select:
SELECT COUNT(*) AS CNT
FROM MEMBER
WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE MEMBER
SET MEMBER_NAME = #{memberName}, 

	DEPT_NO = #{deptNo}, 
	RANK_NO = #{rankNo}, 
	EMAIL = #{email}, 
	PHONE = #{phone}, 
	TELEPHONE = #{telephone}, 
	ENROLL_DATE = #{enrollDate}, 

		,MEMBER_NO = #{memberNo}



		,FILEPATH = #{filePath}


		,FILE = #{file}

WHERE MEMBER_NO = #{memberNo_old}

mapper.select:
SELECT COUNT(*) AS CNT
FROM DEPT
WHERE DEPT_NAME = #{deptName}

mapper.update:
UPDATE DEPT
SET DEPT_NAME = #{deptName}
WHERE DEPT_NO = #{deptNo}
