mapper.select:


          select a.*
	          from(
	          select *
				from to_do
				where "CHECK" =  'N'
				AND MEMBER_NO = #{memberNo}
	                ) a
	        where rownum <= 7

mapper.update:
update to_do set 
"CHECK" = 'Y'
where board_no = #{boardNo}
and member_no = #{memberNo}
and "CONTENT" = #{content}
